mapper.select:
select count(*) from fproduct




        pname like '%' #{pname} '%'





        and paddress like '%' #{paddress} '%'





        and mid like '%' #{mid} '%'






        and createDate > #{createDate}





        and endDate < #{endDate}

select f.pid, f.pname, f.createDate, f.endDate, m.mname as mid, f.repertory, f.price, f.paddress from fproduct f join merchant m on f.mid=m.mid
f.pname like '%' #{pname} '%'
and f.paddress like '%' #{paddress} '%'
and f.mid like '%' #{mid} '%'
and f.createDate > #{createDate}
and f.endDate < #{endDate}
order by f.createDate desc limit #{skipCount} , #{pageSize}
